				\begin{document}$$ \begin{aligned} & {\text{TyG}} = \ln \left[ {\frac{{{\text{triglycerides~}}\left( {{\text{mg}}/{\text{dl}}} \right) \times {\text{glucose~}}\left( {{\text{mg}}/{\text{dl}}} \right)}}{2}} \right] \\ & {\text{WHtR}} = \frac{{{\text{waist}}\,{\text{circumference}}}}{{{\text{height}}}} \\ & {\text{TyG}} - {\text{WHtR}} = {\text{TyG}} \times {\text{WHtR}} \\ & {\text{cumulative~}}\,{\text{TyG}} - {\text{WHtR}} = \frac{{\left( {{\text{TyG}} - {\text{WHtR}}2012 + {\text{TyG}} - {\text{WHtR}}2015} \right)}}{2} \\ & \,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\,\quad \times {\text{time}}\left( {2015 - 2012} \right) \\ \end{aligned} $$\end{document}
